    }
  }

  return (
    <section>
      <div className="game-board">
        {numbers.map((number, index) => {
          return (
            <MemoryCard
              number={number}
              key={index}
              firstCard={firstCard}
              secondCard={secondCard}
              setFirstCard={setFirstCard}
              setSecondCard={setSecondCard}
              compareCards={compareCards}
            />
          );
        })}
      </div>
      <div>
        <p>par: {pairs}</p>
      </div>
    </section>
  );
}

export default GameBoard;
