import React, { useState } from "react";

//TODO
//Denna komponent ska vara ett memorykort som innehåller en siffra
//Den ska ha ett klickevent som håller koll på vilket kort du har klickat på
//När man klickar på det ska du dynamiskt lägga till CSS-klassen "flip"

function MemoryCard(props) {
  // const [isFlipped, setIsFlipped] = useState(false);
  const [first, setFirst] = useState(0);
  const [second, setSecond] = useState(0);

  function handleClick(e) {
    // props.setIsFlipped(true);
    e.target.offsetParent.className = "memory-card flip";

    if (props.firstCard === 0) {
      let firstCard = e.target.id;
      props.setFirstCard(e.target.id);
    } else {
      let secondCard = e.target.id;
      props.setSecondCard(e.target.id);

      console.log(firstCard, props.secondCard);
    }
  }

  function disable() {}

  return (
    <article
      className={`memory-card ${props.isFlipped ? "flip" : ""}`}
      onClick={props.isFlipped ? disable : handleClick}
    >
      <span className="front">{props.number}</span>
      <span className="back" id={props.number}>
        Memory
      </span>
    </article>
  );
}

export default MemoryCard;
