import React, { useEffect, useState } from "react";

//TODO
//Denna komponent ska vara ett memorykort som innehåller en siffra
//Den ska ha ett klickevent som håller koll på vilket kort du har klickat på
//När man klickar på det ska du dynamiskt lägga till CSS-klassen "flip"

function MemoryCard(props) {
  const [isFlipped, setIsFlipped] = useState(false);
  const [firstCard, setFirstCard] = useState(0);
  const [secondCard, setSecondCard] = useState(0);

  console.log(firstCard);
  function handleClick(e) {
    setIsFlipped(!isFlipped);
    // props.updateScore(1);

    // console.log("target " + e.target.id);

    if (firstCard === 0) {
      firstCard = e.target.id;
      console.log(firstCard);
    } else {
      secondCard = e.target.id;
      setTimeout(function () {
        compareCards();
      }, 2000);
    }
  }

  function compareCards() {
    console.log("första " + firstCard);
    console.log("andra " + secondCard);

    if (props.firstCard === props.secondCard) {
      console.log("lika");
      props.updateScore(1);
    } else {
      console.log("olika");
      setIsFlipped(!isFlipped);
    }

    // props.setFirstCard(0);

    // props.setSecondCard(0);
  }

  return (
    <article
      className={`memory-card ${isFlipped ? "flip" : " "}`}
      onClick={handleClick}
    >
      <span className="front">{props.number}</span>
      <span className="back" id={props.number}>
        Memory
      </span>
    </article>
  );
}

export default MemoryCard;
